import React from "react";
import { Todo } from '../type.ts';

interface Props {
    todo: Todo;
    onToggle: (id) => void;
    onDelete: (id) => void;
}

const TodoItem: React.FC<Props> = ({ todo, onToggle, onDelete }) => {
    return (
        <li className="list-group-item d-flex align-items-start justify">
            <input
            className="form-check-input me-1"
                type="checkbox"
                checked={todo.completed}
                onChange={() => onToggle(todo.id)} />
            <label className="form-check-label stretched-link" style={{ textDecoration: todo.completed ? 'line-through' : 'none' }}>{todo.text}</label>
            <span className="category">{todo.category}</span>
            <button type="button" className="btn btn-outline-primary" onClick={() => onDelete(todo.id)}>Delete</button>
        </li>
    );
};

export default TodoItem
